INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'When in doubt&amp;comma; add more stones. If the stones protest&amp;comma; whisper sweet apologies and add them anyway.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Walls built in silence never betray you. Neither do walls built during lunch&amp;comma; but silence sounds wiser.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'A hall that echoes too much is merely gathering your secrets for later resale.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'If two beams fall at once&amp;comma; it means the third one secretly hated them.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Remember: a roof is just a very ambitious floor that decided to lie about its job.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'A building doesn&amp;apos;t fall down. It simply lies down where it prefers to be.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'If your bridge holds together out of pity&amp;comma; that still counts as engineering.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'People blame crooked walls&amp;comma; but floors are rarely innocent either.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Every great hall begins with one stone&amp;mdash;and ends with a pile of better lessons.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Symmetry is a lie told by sober architects. True strength lies in beautiful failure: a leaning pillar&amp;comma; a stubbornly crooked step&amp;comma; an arch that hums when you aren&amp;apos;t looking. These are marks of a structure that knows it lives in a dangerous world.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'If a ceiling sags&amp;comma; simply call it an &amp;apos;artistic wave&amp;apos; and demand visitors appreciate its boldness. Architecture is mostly about having better excuses than your critics.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Always add more doors than necessary. A building full of doors feels bigger&amp;comma; and if it collapses&amp;comma; you&amp;apos;ll have more exits to choose from.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'A good bet is like a good fish: slippery&amp;comma; unpredictable&amp;comma; and probably stolen.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Never trust a dice you didn&amp;apos;t personally drop into a river and recover during a storm.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Blessed ferry rides double your luck. Unblessed ferry rides double your swimming practice.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'There are two kinds of winners: lucky winners&amp;comma; and loud winners. Be both.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'The house always wins&amp;comma; but sometimes the house floats away.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'When entering a casino&amp;comma; walk sideways and blink often. It confuses the spirits of misfortune&amp;comma; who rely heavily on predictability.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Waddle Wars ain&amp;apos;t for amateurs. You line up side-by-side&amp;comma; and whoever wobbles to the finish line without slippin&amp;apos; wins the pot. Trick is&amp;comma; keep your flippers stuck tight to your sides. Loose flippers catch the breeze&amp;comma; and boom&amp;mdash;you&amp;apos;re last place and owe everyone fish pies.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Watch out for the &amp;apos;Free Clam Toss&amp;apos; game at the harbor. Sure&amp;comma; the first toss is free. The second costs a shell. The third costs your boots. By the fifth toss&amp;comma; you&amp;apos;re legally married to the stall owner&amp;apos;s cousin and expected to stay through the winter feast. Happens more&amp;apos;n you&amp;apos;d think.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides&amp;comma; Fortunes&amp;comma; and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Avoid any penguin offering you &amp;apos;lucky river stones&amp;apos; for your games. They ain&amp;apos;t lucky. They&amp;apos;re coated in slime to make your dice hands slip. Congratulations&amp;comma; you just paid two shells for sabotage and a very wet handshake.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Lay flat. Breathe deep. Ignore the ants. The ground won&amp;apos;t gossip if you&amp;apos;re twitchy.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'A truly good ley line vibrates pleasantly. A bad one hums like a drunk goose.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'If nothing speaks to you after three sunrises&amp;comma; consider bribing the dirt with compliments.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Wearing a cape while listening improves results by up to zero percent&amp;comma; but looks impressive.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'If the soil tells you to start a revolution&amp;comma; politely decline. Dirt is famously impatient.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Before attempting any listening&amp;comma; one must flatten the dirt gently with the palm of the paw. Flattened dirt carries voices farther&amp;comma; much like a calm pond reflects better than a rippling one.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'If after several hours the dirt remains silent&amp;comma; do not despair. Remember&amp;comma; it may simply be weighing your soul in secret. Such judgments take time&amp;comma; and many naps.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Before beginning&amp;comma; one must address the dirt by its proper title: Honored Foundation of All Things. Failure to do so may cause the soil to sulk&amp;comma; delaying any meaningful whispering by several hours or decades. Under no circumstance should one chase small insects during this greeting&amp;comma; no matter how delightfully they scuttle.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner&amp;apos;s Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'One must never demand the dirt&amp;apos;s secrets. Questions should be implied&amp;comma; not spoken. A properly respectful gaze&amp;comma; accompanied by slow blinking&amp;comma; often yields better results than shouting or digging.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason&amp;apos;s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'I built a square so solid not even moss grew. Some called it cursed. I called it proof.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason&amp;apos;s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'If a stone hums at night&amp;comma; it&amp;apos;s either ancient magic or ancient plumbing. I ignore both.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason&amp;apos;s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'Never apologize for a collapsed archway. A true mason knows that gravity itself is the enemy&amp;comma; and losing a battle is not losing the war.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason&amp;apos;s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'First thing you learn is if it don&amp;apos;t wobble a little&amp;comma; it&amp;apos;s bound to snap clean later. Let a wall dance a bit in the wind. Means it&amp;apos;s listenin&amp;apos; to the world proper.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason&amp;apos;s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'Never measure nothin&amp;apos;. Soon as you start measurin&amp;apos;&amp;comma; the numbers start lyin&amp;apos;. Best way to build is by eyeball and gut and cussin&amp;apos; at it regular.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat&amp;apos;s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When among Duckies&amp;comma; refrain from correcting their endless excitement. Simply blink slowly and allow them to interpret it as encouragement. If forced to participate in their games&amp;comma; choose the one requiring the least movement and feign deep strategic thought.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat&amp;apos;s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When among Duckies&amp;comma; permit them their excitable chatter. Nod once to acknowledge their enthusiasm&amp;comma; but resist the urge to bat at their head feathers&amp;comma; no matter how amusingly they bobble. If brightly colored objects are introduced&amp;comma; do not chase them. You are an emissary&amp;comma; not a kitten.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat&amp;apos;s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'Puffins are fond of tales involving grand journeys and great ships. While enduring such stories&amp;comma; avoid leaping onto any rolled-up maps&amp;comma; no matter how inviting they appear. Should you feel the irresistible desire to sprint wildly across the room during a navigation debate&amp;comma; close your eyes and count to twenty-seven.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat&amp;apos;s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When encountering Ripkin&amp;comma; remain composed even if they reek of dust&amp;comma; old magic&amp;comma; and questionable decisions. It is critical not to stare at shimmering artifacts until your pupils dilate uncontrollably. Should one begin levitating something&amp;comma; refrain from pouncing on it. Diplomacy demands patience&amp;comma; not aerial assault.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat&amp;apos;s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'Among Penguins&amp;comma; endure their endless boasting without interruption. If a Penguin produces glittering trophies or coins&amp;comma; resist the noble temptation to swipe them under a table for private inspection.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat&amp;apos;s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When another Cat is present&amp;comma; the situation demands utmost restraint. Do not sprint at each other at full speed without warning. This may frighten the Duckies&amp;comma; confuse the Puffins&amp;comma; and cause the Penguins to begin wagering. Dignity must be preserved at all costs&amp;comma; even if a tail flicks most insultingly.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time &amp;ndash; A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Puffins are shipbuilders&amp;comma; see&amp;comma; real serious types. When we first stumbled into their docks&amp;comma; we tried to race their boats &amp;mdash; from the water&amp;comma; no paddles. Three Duckies sank&amp;comma; two floated away&amp;comma; and one declared himself &amp;apos;Admiral of Good Times.&amp;apos; We were invited to dinner by sunset. Turns out&amp;comma; laughing in the face of logic wins you a lot of friends in puffin towns.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time &amp;ndash; A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Ripkin looked mighty scary at first &amp;mdash; robes&amp;comma; old magic&amp;comma; all that brooding nonsense. We figured the best way to break the ice was a song. Trouble is&amp;comma; nobody knew the same song&amp;comma; so it turned into a lot of yelling about fish. Ripkin thought it was some kinda war ritual and gave us tribute to leave. We took the tribute and came back a week later to say thank you. Somehow&amp;comma; that made us allies.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time &amp;ndash; A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Penguins like games. We like games. Shoulda been simple. First thing we did? Invented a race called &amp;apos;Waddle Smash&amp;apos; &amp;mdash; no rules&amp;comma; just wobble fast and scream. Half of Drift Carnival joined in. I personally dislocated a flipper and a penguin bishop blessed the whole thing as &amp;apos;an act of pure river spirit.&amp;apos; Made a lot of friends...and bruises.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time &amp;ndash; A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'There&amp;apos;s a place called Shellbend that thinks we saved &amp;apos;em from a flood. Truth is&amp;comma; we built a dam by accident trying to stack river stones into a &amp;apos;friendship tower.&amp;apos; The whole thing collapsed sideways&amp;comma; blocked the stream&amp;comma; and saved the village. They named the stream after us. &amp;apos;Duckwater&amp;comma;&amp;apos; they call it. Which sounds heroic.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time II &amp;ndash; Even More Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Found a village way up north what looked half-frozen and half-forgotten. Thought we&amp;apos;d bring &amp;apos;em cheer&amp;comma; so we threw a festival in their square without askin&amp;apos;. Lit torches&amp;comma; danced till the snow cracked. Turned out it was their month of silence. We were nearly exiled&amp;comma; but after we slipped down Main Street playing sled races&amp;comma; they decided we were &amp;apos;spirit creatures&amp;apos; and made us honorary villagers. Haven&amp;apos;t been back since. Too cold.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time II &amp;ndash; Even More Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'There&amp;apos;s a place south called Hollowsheen. We just wanted to camp there one night&amp;comma; but someone knocked over a torch stand and set the whole orchard glowing. By morning&amp;comma; they thought it was a miracle and declared a new holiday: &amp;apos;The Day of Bright Feathers.&amp;apos; They still celebrate it. We still have no idea what we&amp;apos;re supposedly sacred for.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time II &amp;ndash; Even More Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'One time we crashed a Puffin solstice party by accident&amp;comma; all dressed up in extra cloaks and shiny medals we found. Looked real impressive&amp;comma; apparently. They thought we were &amp;apos;Sky Heralds&amp;apos; sent to bless the harvest. We blessed everything we could find: pies&amp;comma; ships&amp;comma; one confused goat. Got a feast and a ship named after us.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time III &amp;ndash; The Legend Wobbles On', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Once wandered into a canyon&amp;comma; found a spring glowing all misty and blue. We swam in it. Did laps. Played dive-and-splash games. Turns out it was sacred to three different tribes. They saw the ripples and figured we were water spirits &amp;apos;testing the purity.&amp;apos; So now we&amp;apos;re listed in their histories as &amp;apos;Bathers of Truth.&amp;apos; Got a statue and all.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time III &amp;ndash; The Legend Wobbles On', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Music contest at Starwharf&amp;comma; and we Duckies showed up with nothing but our beaks and stompy feet. Made a racket loud enough to scare the dock pigeons clear into next week. Judges called it a &amp;apos;primal expression of true heart.&amp;apos; We called it &amp;apos;trying not to fall off the stage.&amp;apos; Won third place and honorary mariner hats.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV &amp;ndash; Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'First tried it on a bet&amp;comma; tying three hammocks together to cross a ravine. Two fell&amp;comma; one tangled&amp;comma; and somehow the third made it. Puffins saw it and called it &amp;apos;ingenious.&amp;apos; We called it &amp;apos;Tuesday.&amp;apos; Now every proper mountain trail&amp;apos;s got rope bridges.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV &amp;ndash; Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Once camped so poorly on a merchant road that we blocked the day traffic entirely. Traders had to stay overnight&amp;comma; started selling by torchlight out of boredom. Next thing you know&amp;comma; night markets spring up across half the continent. Some say clever commerce. We say clever Duckies with very confusing tent layouts.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV &amp;ndash; Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Trying to cross a marsh in a straight line after a festival was never gonna work. We slipped&amp;comma; slid&amp;comma; and laughed our feathers off all the way to the far ridge. Locals decided it looked like a grand sport. Now they hold Wobble book_author_races every spring. Prize for the one who falls the least and laughs the most. Naturally&amp;comma; Duckies are reigning champions.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV &amp;ndash; Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'We once drew directions to a fish pie stand on a big flat stone using mud and a stick. Some cat copied it down&amp;comma; and boom. First town map in Aeldwaddle history. Cats pretend it was a &amp;apos;sacred cartographer&amp;apos; who made it. It was really just six hungry Duckies trying not to miss lunch.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V &amp;ndash; It&amp;apos;ll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Fishing tournament&amp;comma; big prizes&amp;comma; important guests. We entered with nets&amp;comma; buckets&amp;comma; and what we thought were &amp;apos;magic bait.&amp;apos; Turns out it was spicy peppers. Attracted every river creature except fish. Nets caught a canoe. Won &amp;apos;biggest catch&amp;apos; by accident. Disqualified with honors.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V &amp;ndash; It&amp;apos;ll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Got asked to witness a peace treaty between two rival villages. Real solemn. Real serious. We brought snacks. Halfway through the talks&amp;comma; a Duckie sneezed&amp;comma; flipped the table&amp;comma; and launched the treaty into a ceremonial fire. Somehow made &amp;apos;eternal flames of unity&amp;apos; sound very intentional. Everyone clapped. We bowed.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V &amp;ndash; It&amp;apos;ll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Organized a &amp;apos;quiet parade&amp;apos; for a sick elder. Instructions: no yelling&amp;comma; no stomping. First two minutes&amp;comma; perfect. Then one Duckie tripped&amp;comma; knocked over the tuba wagon&amp;comma; set off five drums&amp;comma; and scared half the town&amp;apos;s ducks into the march. Called it a &amp;apos;march of good spirits.&amp;apos; Ended in cake.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V &amp;ndash; It&amp;apos;ll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Simple request: plant ten trees along a path. Somehow we planted sixty-seven by noon&amp;comma; blocking the road&amp;comma; the river ferry&amp;comma; and someone&amp;apos;s bakery. Celebrated with cider. Locals called it &amp;apos;an unexpected forest miracle&amp;apos; and marked it on maps. Route&amp;apos;s still blocked. You&amp;apos;re welcome.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V &amp;ndash; It&amp;apos;ll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Asked to help at a wizard&amp;apos;s festival. Task: toss glitter dust at the right moment. Instead&amp;comma; one Duckie tripped and hurled the whole bag into the summoning circle&amp;comma; turning the guest speaker into a very angry turnip. Every year since&amp;comma; we gather to eat boiled turnips and politely nod at each other.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V &amp;ndash; It&amp;apos;ll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Witness a peace treaty. Sit still&amp;comma; behave. Easy&amp;comma; right? Until a Duckie sneezed plum tarts directly into both delegations. Talks collapsed into yelling and pastry-throwing. They did sign a treaty&amp;comma; though: an agreement not to invite Duckies to anything important ever again.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'When Penguin pirates raided a coastal inn&amp;comma; the Duckies unleashed a defense involving paper cannons and vigorous bread hurling. This stopped nothing&amp;comma;but they were so entertained they stayed longer than intended. Fortunately&amp;comma; this delay allowed myself and a group of traveling Puffin merchants to arrive. The pirates left with little more than crumbs and bruised pride. One of them even forgot their hat&amp;comma; which was promptly adopted by the Duckies and worn proudly until it became tradition to throw bread at anyone spotted wearing it.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'At a cliffside market&amp;comma; a Ducky named Clover Duckweed attempted to trade a single pebble for a ceremonial sword. Through relentless bargaining&amp;comma; song&amp;comma; and sheer charisma&amp;comma; she succeeded. When I asked her plan for the sword&amp;comma; she said &amp;apos;probably gardening.&amp;apos;', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'I once accompanied a Ducky named Waddleton Proudfloof during a mapping expedition. He mapped three trees&amp;comma; one puddle&amp;comma; and a very convincing shadow. His final report was submitted with flair and three pastries. It remains the most optimistic cartography I have ever witnessed.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Assigned to build a bridge&amp;comma; Duckies formed a council&amp;comma; elected a king&amp;comma; hosted multiple feasts&amp;comma; and ceremoniously leapt across the river instead. The bridge remains theoretical. The leaping tradition&amp;comma; however&amp;comma; has expanded into a two-day festival. I have attended twice. For research.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Given a simple festival race&amp;comma; Duckies constructed gliders to &amp;apos;enhance the experience.&amp;apos; Flight was brief&amp;comma; shrubbery suffered&amp;comma; but applause was thunderous. Several Duckies claimed titles like &amp;apos;Air Commander&amp;apos; and &amp;apos;Master of Leafy Landings.&amp;apos; The judges awarded every participant a very strange note&amp;comma; that only read&amp;comma; &amp;apos;IOU&amp;apos;. Still puzzles me to this day.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Summoned to put out a small bakery fire&amp;comma; the Duckies founded a fire brigade with bright hats and grand titles. The fire extinguished itself long before their first meeting adjourned. Nonetheless&amp;comma; the Duckies continue to meet monthly and host parades on alternate Wednesdays.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Faced with a locked cellar door&amp;comma; Duckies once built a new house around it and declared it a historical artifact. The door remains&amp;comma; unbothered and highly honored. Visitors leave it small offerings: coins&amp;comma; poems&amp;comma; sandwiches. It is without doubt the most celebrated piece of wood in Aeldwaddle.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'During the Great Market Festival&amp;comma; a Ducky tasked with a simple bread delivery declared an &amp;apos;epic quest for the Golden Loaf.&amp;apos; Helmets were crafted from buckets. Maps were drawn (badly). The bread was eventually delivered&amp;comma; albeit heroically stale.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'When asked to extinguish a small fire at a village bakery&amp;comma; the Duckies founded an entirely new fire brigade instead. They wore bright hats&amp;comma; created elaborate titles&amp;comma; and swore oaths of valor&amp;comma; forgotting completely about the fire.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);