INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'When in doubt, add more stones. If the stones protest, whisper sweet apologies and add them anyway.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Walls built in silence never betray you. Neither do walls built during lunch, but silence sounds wiser.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'A hall that echoes too much is merely gathering your secrets for later resale.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'If two beams fall at once, it means the third one secretly hated them.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Remember: a roof is just a very ambitious floor that decided to lie about its job.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'A building doesn’t fall down. It simply lies down where it prefers to be.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'If your bridge holds together out of pity, that still counts as engineering.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'People blame crooked walls, but floors are rarely innocent either.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Every great hall begins with one stone—and ends with a pile of better lessons.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Symmetry is a lie told by sober architects. True strength lies in beautiful failure: a leaning pillar, a stubbornly crooked step, an arch that hums when you aren''t looking. These are marks of a structure that knows it lives in a dangerous world.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'If a ceiling sags, simply call it an ‘artistic wave’ and demand visitors appreciate its boldness. Architecture is mostly about having better excuses than your critics.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Building Things That Mostly Stay Upright', 'Brinn Driftgleam', 'Ripkin', 'Hallcrafter', 'Always add more doors than necessary. A building full of doors feels bigger, and if it collapses, you''ll have more exits to choose from.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'A good bet is like a good fish: slippery, unpredictable, and probably stolen.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Never trust a dice you didn’t personally drop into a river and recover during a storm.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Blessed ferry rides double your luck. Unblessed ferry rides double your swimming practice.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'There are two kinds of winners: lucky winners, and loud winners. Be both.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'The house always wins, but sometimes the house floats away.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'When entering a casino, walk sideways and blink often. It confuses the spirits of misfortune, who rely heavily on predictability.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Waddle Wars ain’t for amateurs. You line up side-by-side, and whoever wobbles to the finish line without slippin'' wins the pot. Trick is, keep your flippers stuck tight to your sides. Loose flippers catch the breeze, and boom—you''re last place and owe everyone fish pies.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Watch out for the ‘Free Clam Toss’ game at the harbor. Sure, the first toss is free. The second costs a shell. The third costs your boots. By the fifth toss, you''re legally married to the stall owner’s cousin and expected to stay through the winter feast. Happens more''n you''d think.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Tides, Fortunes, and Other Things I Yell About', 'Brask Gutterwake', 'Penguin', 'Dredgemaster', 'Avoid any penguin offering you ‘lucky river stones’ for your games. They ain’t lucky. They''re coated in slime to make your dice hands slip. Congratulations, you just paid two shells for sabotage and a very wet handshake.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Lay flat. Breathe deep. Ignore the ants. The ground won''t gossip if you’re twitchy.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'A truly good ley line vibrates pleasantly. A bad one hums like a drunk goose.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'If nothing speaks to you after three sunrises, consider bribing the dirt with compliments.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Wearing a cape while listening improves results by up to zero percent, but looks impressive.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'If the soil tells you to start a revolution, politely decline. Dirt is famously impatient.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Before attempting any listening, one must flatten the dirt gently with the palm of the paw. Flattened dirt carries voices farther, much like a calm pond reflects better than a rippling one.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'If after several hours the dirt remains silent, do not despair. Remember, it may simply be weighing your soul in secret. Such judgments take time, and many naps.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'Before beginning, one must address the dirt by its proper title: Honored Foundation of All Things. Failure to do so may cause the soil to sulk, delaying any meaningful whispering by several hours or decades. Under no circumstance should one chase small insects during this greeting, no matter how delightfully they scuttle.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Beginner's Guide to Staring at Dirt Until It Speaks', 'Velric Glimmershade', 'Cat', 'Runechaser', 'One must never demand the dirt’s secrets. Questions should be implied, not spoken. A properly respectful gaze, accompanied by slow blinking, often yields better results than shouting or digging.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason’s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'I built a square so solid not even moss grew. Some called it cursed. I called it proof.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason’s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'If a stone hums at night, it’s either ancient magic or ancient plumbing. I ignore both.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason’s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'Never apologize for a collapsed archway. A true mason knows that gravity itself is the enemy, and losing a battle is not losing the war.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason’s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'First thing you learn is if it don’t wobble a little, it’s bound to snap clean later. Let a wall dance a bit in the wind. Means it’s listenin’ to the world proper.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('Of Stones and Slander: A Mason’s Grievances', 'Vrelk Shardbone', 'Ripkin', 'Mason', 'Never measure nothin’. Soon as you start measurin’, the numbers start lyin’. Best way to build is by eyeball and gut and cussin’ at it regular.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat’s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When among Duckies, refrain from correcting their endless excitement. Simply blink slowly and allow them to interpret it as encouragement. If forced to participate in their games, choose the one requiring the least movement and feign deep strategic thought.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat’s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When among Duckies, permit them their excitable chatter. Nod once to acknowledge their enthusiasm, but resist the urge to bat at their head feathers, no matter how amusingly they bobble. If brightly colored objects are introduced, do not chase them. You are an emissary, not a kitten.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat’s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'Puffins are fond of tales involving grand journeys and great ships. While enduring such stories, avoid leaping onto any rolled-up maps, no matter how inviting they appear. Should you feel the irresistible desire to sprint wildly across the room during a navigation debate, close your eyes and count to twenty-seven.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat’s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When encountering Ripkin, remain composed even if they reek of dust, old magic, and questionable decisions. It is critical not to stare at shimmering artifacts until your pupils dilate uncontrollably. Should one begin levitating something, refrain from pouncing on it. Diplomacy demands patience, not aerial assault.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat’s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'Among Penguins, endure their endless boasting without interruption. If a Penguin produces glittering trophies or coins, resist the noble temptation to swipe them under a table for private inspection.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('The Art of Dignified Society: A Cat’s Conduct Among the Less Fortunate', 'Veyra Mapleshade', 'Cat', 'Diplomat', 'When another Cat is present, the situation demands utmost restraint. Do not sprint at each other at full speed without warning. This may frighten the Duckies, confuse the Puffins, and cause the Penguins to begin wagering. Dignity must be preserved at all costs, even if a tail flicks most insultingly.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time – A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Puffins are shipbuilders, see, real serious types. When we first stumbled into their docks, we tried to race their boats — from the water, no paddles. Three Duckies sank, two floated away, and one declared himself ‘Admiral of Good Times.’ We were invited to dinner by sunset. Turns out, laughing in the face of logic wins you a lot of friends in puffin towns.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time – A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Ripkin looked mighty scary at first — robes, old magic, all that brooding nonsense. We figured the best way to break the ice was a song. Trouble is, nobody knew the same song, so it turned into a lot of yelling about fish. Ripkin thought it was some kinda war ritual and gave us tribute to leave. We took the tribute and came back a week later to say thank you. Somehow, that made us allies.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time – A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Penguins like games. We like games. Shoulda been simple. First thing we did? Invented a race called ‘Waddle Smash’ — no rules, just wobble fast and scream. Half of Drift Carnival joined in. I personally dislocated a flipper and a penguin bishop blessed the whole thing as ‘an act of pure river spirit.’ Made a lot of friends...and bruises.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time – A History of Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'There’s a place called Shellbend that thinks we saved ‘em from a flood. Truth is, we built a dam by accident trying to stack river stones into a ''friendship tower.'' The whole thing collapsed sideways, blocked the stream, and saved the village. They named the stream after us. ‘Duckwater,’ they call it. Which sounds heroic.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time II – Even More Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Found a village way up north what looked half-frozen and half-forgotten. Thought we’d bring ‘em cheer, so we threw a festival in their square without askin’. Lit torches, danced till the snow cracked. Turned out it was their month of silence. We were nearly exiled, but after we slipped down Main Street playing sled races, they decided we were ''spirit creatures'' and made us honorary villagers. Haven’t been back since. Too cold.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time II – Even More Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'There’s a place south called Hollowsheen. We just wanted to camp there one night, but someone knocked over a torch stand and set the whole orchard glowing. By morning, they thought it was a miracle and declared a new holiday: ‘The Day of Bright Feathers.’ They still celebrate it. We still have no idea what we’re supposedly sacred for.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time II – Even More Meets', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'One time we crashed a Puffin solstice party by accident, all dressed up in extra cloaks and shiny medals we found. Looked real impressive, apparently. They thought we were ‘Sky Heralds’ sent to bless the harvest. We blessed everything we could find: pies, ships, one confused goat. Got a feast and a ship named after us.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time III – The Legend Wobbles On', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Once wandered into a canyon, found a spring glowing all misty and blue. We swam in it. Did laps. Played dive-and-splash games. Turns out it was sacred to three different tribes. They saw the ripples and figured we were water spirits ‘testing the purity.’ So now we''re listed in their histories as ''Bathers of Truth.'' Got a statue and all.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time III – The Legend Wobbles On', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Music contest at Starwharf, and we Duckies showed up with nothing but our beaks and stompy feet. Made a racket loud enough to scare the dock pigeons clear into next week. Judges called it a ‘primal expression of true heart.’ We called it ‘trying not to fall off the stage.’ Won third place and honorary mariner hats.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV – Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'First tried it on a bet, tying three hammocks together to cross a ravine. Two fell, one tangled, and somehow the third made it. Puffins saw it and called it ''ingenious.'' We called it ''Tuesday.'' Now every proper mountain trail''s got rope bridges.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV – Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Once camped so poorly on a merchant road that we blocked the day traffic entirely. Traders had to stay overnight, started selling by torchlight out of boredom. Next thing you know, night markets spring up across half the continent. Some say clever commerce. We say clever Duckies with very confusing tent layouts.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV – Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Trying to cross a marsh in a straight line after a festival was never gonna work. We slipped, slid, and laughed our feathers off all the way to the far ridge. Locals decided it looked like a grand sport. Now they hold Wobble book_author_races every spring. Prize for the one who falls the least and laughs the most. Naturally, Duckies are reigning champions.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time IV – Great Ideas We Definitely Meant', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'We once drew directions to a fish pie stand on a big flat stone using mud and a stick. Some cat copied it down, and boom. First town map in Aeldwaddle history. Cats pretend it was a ‘sacred cartographer’ who made it. It was really just six hungry Duckies trying not to miss lunch.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V – It'll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Fishing tournament, big prizes, important guests. We entered with nets, buckets, and what we thought were ‘magic bait.’ Turns out it was spicy peppers. Attracted every river creature except fish. Nets caught a canoe. Won ‘biggest catch’ by accident. Disqualified with honors.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V – It'll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Got asked to witness a peace treaty between two rival villages. Real solemn. Real serious. We brought snacks. Halfway through the talks, a Duckie sneezed, flipped the table, and launched the treaty into a ceremonial fire. Somehow made ‘eternal flames of unity’ sound very intentional. Everyone clapped. We bowed.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V – It'll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Organized a ‘quiet parade’ for a sick elder. Instructions: no yelling, no stomping. First two minutes, perfect. Then one Duckie tripped, knocked over the tuba wagon, set off five drums, and scared half the town’s ducks into the march. Called it a ‘march of good spirits.’ Ended in cake.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V – It'll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Simple request: plant ten trees along a path. Somehow we planted sixty-seven by noon, blocking the road, the river ferry, and someone''s bakery. Celebrated with cider. Locals called it ''an unexpected forest miracle'' and marked it on maps. Route''s still blocked. You''re welcome.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V – It'll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Asked to help at a wizard’s festival. Task: toss glitter dust at the right moment. Instead, one Duckie tripped and hurled the whole bag into the summoning circle, turning the guest speaker into a very angry turnip. Every year since, we gather to eat boiled turnips and politely nod at each other.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('A Ducking Good Time V – It'll Be Fine', 'Marnin Oldflap', 'Ducky', 'Elder Storyteller', 'Witness a peace treaty. Sit still, behave. Easy, right? Until a Duckie sneezed plum tarts directly into both delegations. Talks collapsed into yelling and pastry-throwing. They did sign a treaty, though: an agreement not to invite Duckies to anything important ever again.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'When Penguin pirates raided a coastal inn, the Duckies unleashed a defense involving paper cannons and vigorous bread hurling. This stopped nothing,but they were so entertained they stayed longer than intended. Fortunately, this delay allowed myself and a group of traveling Puffin merchants to arrive. The pirates left with little more than crumbs and bruised pride. One of them even forgot their hat, which was promptly adopted by the Duckies and worn proudly until it became tradition to throw bread at anyone spotted wearing it.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'At a cliffside market, a Ducky named Clover Duckweed attempted to trade a single pebble for a ceremonial sword. Through relentless bargaining, song, and sheer charisma, she succeeded. When I asked her plan for the sword, she said ''probably gardening.''', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'I once accompanied a Ducky named Waddleton Proudfloof during a mapping expedition. He mapped three trees, one puddle, and a very convincing shadow. His final report was submitted with flair and three pastries. It remains the most optimistic cartography I have ever witnessed.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Assigned to build a bridge, Duckies formed a council, elected a king, hosted multiple feasts, and ceremoniously leapt across the river instead. The bridge remains theoretical. The leaping tradition, however, has expanded into a two-day festival. I have attended twice. For research.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Given a simple festival race, Duckies constructed gliders to ''enhance the experience.'' Flight was brief, shrubbery suffered, but applause was thunderous. Several Duckies claimed titles like ''Air Commander'' and ''Master of Leafy Landings.'' The judges awarded every participant a very strange note, that only read, ''IOU''. Still puzzles me to this day.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Summoned to put out a small bakery fire, the Duckies founded a fire brigade with bright hats and grand titles. The fire extinguished itself long before their first meeting adjourned. Nonetheless, the Duckies continue to meet monthly and host parades on alternate Wednesdays.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'Faced with a locked cellar door, Duckies once built a new house around it and declared it a historical artifact. The door remains, unbothered and highly honored. Visitors leave it small offerings: coins, poems, sandwiches. It is without doubt the most celebrated piece of wood in Aeldwaddle.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'During the Great Market Festival, a Ducky tasked with a simple bread delivery declared an ''epic quest for the Golden Loaf.'' Helmets were crafted from buckets. Maps were drawn (badly). The bread was eventually delivered, albeit heroically stale.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO excerpts (book_title, book_author, book_author_race, book_author_role, book_excerpt, created_at, updated_at) VALUES ('How to Stay Afloat and Other Obvious Advice', 'Elder Catteridge Greywhisk', 'Cat', 'Cartographer and Historian of the Great Archives', 'When asked to extinguish a small fire at a village bakery, the Duckies founded an entirely new fire brigade instead. They wore bright hats, created elaborate titles, and swore oaths of valor, forgotting completely about the fire.', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);